) values ( #{objtNo} , #{objtTp} , #{objtNm} , #{custNm} , CASE WHEN #{custTel1} = '' THEN NULL ELSE #{custTel1} END , CASE WHEN #{custTel2} = '' THEN NULL ELSE #{custTel2} END , CASE WHEN #{custTel3} = '' THEN NULL ELSE #{custTel3} END , #{buildNm} , #{businessNm} , CASE WHEN #{area} = '' THEN 0 ELSE #{area} END , CASE WHEN #{realArea} = '' THEN 0 ELSE #{realArea} END , #{saleTp} , CASE WHEN #{bargainAmt} = '' THEN 0 ELSE #{bargainAmt} END , CASE WHEN #{depositAmt} = '' THEN 0 ELSE #{depositAmt} END , CASE WHEN #{monthlyAmt} = '' THEN 0 ELSE #{monthlyAmt} END , #{dong} , CASE WHEN #{floor} = '' THEN NULL ELSE #{floor} END , #{directionTp} , #{dueDt} , CASE WHEN #{roomCnt} = '' THEN NULL ELSE #{roomCnt} END , CASE WHEN #{bathCnt} = '' THEN NULL ELSE #{bathCnt} END , #{ondolYn} , #{availableTp} , CASE WHEN #{availableDt} = '' THEN NULL ELSE #{availableDt} END , #{conditionTp} , #{memo} , #{activeTp} , CASE WHEN #{advertiseYn} = 'Y' THEN 'Y' ELSE 'N' END , CASE WHEN #{publicYn} = 'Y' THEN 'Y' ELSE 'N' END , CASE WHEN #{parcelArea} = '' THEN 0 ELSE #{parcelArea} END , CASE WHEN #{rightAmt} = '' THEN 0 ELSE #{rightAmt} END , CASE WHEN #{manageAmt} = '' THEN 0 ELSE #{manageAmt} END , CASE WHEN #{interiorYn} = '' THEN NULL ELSE #{interiorYn} END , CASE WHEN #{surtaxYn} = '' THEN NULL ELSE #{surtaxYn} END , CASE WHEN #{parcelAmt} = '' THEN 0 ELSE #{parcelAmt} END , CASE WHEN #{premiumAmt} = '' THEN 0 ELSE #{premiumAmt} END
